import { configureStore, createSlice } from "@reduxjs/toolkit";
const cartInitialState = { isCartOpen: false };
const showCartSlice = createSlice({
  name: "showCart",
  initialState: cartInitialState,
  reducers: {
    changeShowCart(state) {
      if (state.isCartOpen == false) state.isCartOpen = true;
      else state.isCartOpen == false;
    },
  },
});

const store = configureStore({ reducer: { cart: showCartSlice.reducer } });
export const showCartAction=showCartSlice.actions;
export default store;
