import { useDispatch, useSelector } from 'react-redux';
import classes from './CartButton.module.css';
import { showCartAction } from '../../store';

const CartButton = (props) => {
  const dispatch=useDispatch();
  const cart=useSelector(state=>state.cart.isCartOpen);
  const openHandler=()=>{
    if(cart=="true")
    dispatch(showCartAction.changeShowCart("false"));
  else
  dispatch(showCartAction.changeShowCart("true"));
  }
  return (
    <button className={classes.button} onClick={openHandler}>
      <span>My Cart</span>
      <span className={classes.badge}>1</span>
    </button>
  );
};

export default CartButton;
